package javaconcepts;

public class ArrayTestt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int p[] = { 1, 3, 4, 5, 22, 56, 89, 90 };

		for (int i = 7; i >= 0; i--)// reverse u shd make use of > and decrement
		{
			System.out.println(p[i]);
		}
		int j = 7;
		while (j >= 0) {
			System.out.println(p[j]);
			j--;
		}
		System.out.println("*******");
		int k = 7;
		do {
			System.out.println(p[k]);
			k--;

		} while (k >= 0);
		
//Using for each how can we reverse a array ..
//Using which date type we can store DOB in JAVA 
	}

}
